import React,{useState} from 'react'
import { AppRouter } from './AppRouter'
import { UserContext } from './UserContext'

export const MainApp = () => {

  const [user, setUser] = useState({});



  return (
    <UserContext.Provider value={{
      user:user,
      setUser:setUser
    }}>
      <AppRouter />
    </UserContext.Provider>
    
  )
}
