import React, { useState } from 'react'
import './counter.css'
export default function CounterApp() {

  const [counter, setCounter] = useState({
    counter1: 10,
    counter2:20
  });


  console.log(counter);
  
  return (
    <>
      {/* <h1>counter {counter}</h1> */}
      <hr />

      <button className="btn btn-primary"
        onClick={()=>
          setCounter(counter+1)
        }
      >
        +1
      </button>
    </>
  )
}
