import {useState} from 'react'

export const useCounter = (initialState = 10) => {
  const [state, setstate] = useState(initialState);//10

  const increment = () =>{
    state(state +1 )
  }

  const decrement = () =>{
    state(state + 1)
  }


  return {
    state,
    increment,
    decrement
  };
}
