const initialState = [{
  id:1,
  todo:'Comprar pan',
  donde:false
}];

const todoReducer = (state = initialState, action) =>{

  return state;
}

let todos = todoReducer();

const newTodo = {
  id:2,
  todo:'comprar leche',
  done:false
}

const agregarTodoAction = {
  type:'agregar',
  payload:newTodo
}


todos = todoReducer(todos, agregarTodoAction);







console.log(todos);
