import React, { useEffect } from 'react'

export const Message = () => {
  useEffect(() => {
    
    window.addEventListener('mousemove',(e)=>{
      const coors = {x :e.x, y:e.y};
      console.log(coors);
    });


    return () => {
      console.log("componente desmontado");
    }
  }, [])

  return (
    <div>
      <h3>Eres genial!</h3>
    </div>
  )
}
