import React,{useCallback} from 'react'
import { Hijo } from './Hijo'
import { useState } from 'react';

import '../02-useEffect/effects.css'

export const Padre = () => {

    const numeros = [2,4,6,8,10];
    const [valor, setValor] = useState(0);


    const increment = useCallback(( num ) => {
        setValor( c=>c+num );
    },[setValor])


    return (
        <div>
            <h1>Padre</h1>
            <p> Total: { valor } </p>

            <hr />

            {
                numeros.map( n => (
                    <Hijo 
                        key={ n }
                        numero={ n }
                        increment={increment}
                    />
                ))
            }
            {/* <Hijo /> */}
        </div>
    )
}
