export const useFetch = () =>{

  const [state, setState] = useState({data:null, loading:true, error:null});

  useEffect(()=>{
    fetch(url)
      .then(respo=>respo.json())
      .then(data=>{
        
        setState({
          loading:false,
          error:null,
          data
        })

      });

  },[url])

  return state;

}
