import {useState, useEffect, useRef} from 'react';

export const useFetch = (url) =>{

  const [state, setState] = useState({data:null, loading:true, error:null});
  
 



   useEffect(()=>{
    setState({data:null, loading:true, error:null})

   
    
    fetch(url)
      .then(respo=>respo.json())
      .then(data=>{
        
        setTimeout(()=>{
          setState({
            loading:false,
            error:null,
            data
          })
        },400)
          
       
  
      });
  },[url]) 

  

  return state;

}
