import {useState} from 'react'

export const useCounter = (initialState = 10) => {
  const [state, setState] = useState(initialState);//10

  const increment = (factoor) =>{
    setState(state + factoor)
  }

  const decrement = (factoor) =>{
    setState(state - factoor)
  }

  const reset = ()=>{
    setState(initialState)
  }

  return {
    state,
    increment,
    decrement,
    reset
  };
}
